mapper.select:
select * from (select row_number() over(order by aIdx desc) as rownum, s.* from articles s where bIdx=#{bIdx} and status != 1
and ${searchType} like CONCAT('%',#{searchValue},'%')
order by aIdx desc) a where rownum >= #{start} and rownum <= #{end}

mapper.update:
update articles	set title=#{title},content=#{content}
where aidx=#{aIdx}
and bidx=#{bIdx}

update articles	set title=#{title},content=#{content}
where aidx=#{aIdx}
and bidx=#{bIdx}

mapper.insert:
insert into liked_articles(
	aIdx,
	bIdx,
	mIdx
)values(
	#{aIdx},
	#{bIdx},
	#{mIdx}
)

mapper.select:
select * from
(SELECT

  @rownum:=@rownum+1 as rownum, 
  art.*
FROM articles art,(select @rownum:=0) rn_art

where bIdx=#{bIdx}

and ${searchType} like CONCAT('%',#{searchValue},'%')


and status=#{status}

order by aidx desc) list

where rownum>=#{start} and rownum <=#{end}

select count(aIdx)
from articles
where bidx=#{bIdx}

and ${searchType} like CONCAT('%',#{searchValue},'%')


and status=#{status}
