mapper.select:
select email, regkey, deadline
from emailreg
where email = #{email}

select tel, regkey, deadline
from telreg
where tel = #{tel}

select m.msgidx ,m.sendertype ,m.sender , case when m.sendertype = 'general' then (select gm.nickname from general_members gm where gm.midx = m.sender ) when m.sendertype = 'host' then (select hm.nickname from host_members hm where hm.midx = m.sender) end as senderNickname ,m.receivertype ,m.receiver , case when m.receivertype = 'general' then (select gm.nickname from general_members gm where gm.midx = m.receiver ) when m.receivertype = 'host' then (select hm.nickname from host_members hm where hm.midx = m.receiver) end as receiverNickname ,m.content ,m.sentdate ,m.status from messages m
where m.receiver = #{receiver} and m.receiverType = #{receiverType}
where m.sender = #{sender} and m.senderType = #{senderType}
order by m.msgidx desc limit #{start},14

mapper.update:
update general_members set
	point = #{balance}
	where midx = #{mIdx}
